import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const mapContainerStyle = { width: '100%', height: '400px' };

const GoogleMapComponent = () => {
  const [shops, setShops] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3004/shops')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setShops(data);
        console.log('Shops data:', data);

        // Calculate the average latitude and longitude of all shops
        const averageLat = data.reduce((sum, shop) => sum + shop.lat, 0) / data.length;
        const averageLng = data.reduce((sum, shop) => sum + shop.lng, 0) / data.length;
        console.log('Average Lat:', averageLat);
        console.log('Average Lng:', averageLng);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  // Set the center to the average coordinates
  const center = {
    lat: isNaN(averageLat) ? 0 : averageLat,
    lng: isNaN(averageLng) ? 0 : averageLng,
  };

  console.log('Center:', center);

  return (
    <LoadScript googleMapsApiKey="YOUR_API_KEY">
      <GoogleMap mapContainerStyle={mapContainerStyle} center={center} zoom={10}>
        {shops.map(shop => (
          <Marker
            key={shop.id}
            position={{ lat: shop.lat, lng: shop.lng }}
          />
        ))}
      </GoogleMap>
    </LoadScript>
  );
};

export default GoogleMapComponent;
