import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';



const BASE_URL = 'http://localhost:3001/api/banners';

const BannerManagement = () => {
  // ... (other state and useEffect)

const handleCreateBanner = async () => {
    try {
    const formData = new FormData();
    formData.append('img_brand_url', newBanner.src1);
    formData.append('img_promotion_url', newBanner.img_promotion);
    formData.append('content', newBanner.content);
    formData.append('topic', newBanner.topic);

    await axios.post(BASE_URL, formData);
    setNewBanner({
        img_brand_url: '',
        img_promotion: null,
        content: '',
        topic: '',});
    fetchBanners();
    } catch (error) {
    console.error('Error creating banner:', error);
    }
};

  // ... (other functions)

return (
    <Container>
    <h1>Banner Management</h1>

    <Form>
        <Row>
        <Col>
            <Form.Group controlId="formImgBrandUrl">
            <Form.Label>Image Brand URL</Form.Label>
            <Form.Control
                type="text"
                placeholder="Enter Image Brand URL"
                value={newBanner.img_brand_url}
                onChange={(e) => setNewBanner({ ...newBanner, img_brand_url: e.target.value })}/>
            </Form.Group>
        </Col>
        <Col>
            <Form.Group controlId="formImgPromotion">
            <Form.Label>Image Promotion</Form.Label>
            <Form.Control type="file" onChange={handleImageChange} />
            </Form.Group>
        </Col>
        <Col>
            <Form.Group controlId="formContent">
            <Form.Label>Content</Form.Label>
            <Form.Control
                as="textarea"
                rows={3}
                placeholder="Enter Content"
                value={newBanner.content}
                onChange={(e) => setNewBanner({ ...newBanner, content: e.target.value })}/>
            </Form.Group>
        </Col>
        <Col>
            <Form.Group controlId="formTopic">
            <Form.Label>Topic</Form.Label>
            <Form.Control
                type="text"
                placeholder="Enter Topic"
                value={newBanner.topic}
                onChange={(e) => setNewBanner({ ...newBanner, topic: e.target.value })}/>
            </Form.Group>
        </Col>
        </Row>

        <Button variant="primary" onClick={handleCreateBanner}>Create Banner</Button>
    </Form>

    <ul>
        {banners.map((banner) => (
        <li key={banner.id}>
            {banner.img_brand_url} - {banner.content} - {banner.topic}
            <Button variant="info" onClick={() => handleUpdateBanner(banner.id, { content: 'Updated Content' })}>Update</Button>
            <Button variant="danger" onClick={() => handleDeleteBanner(banner.id)}>Delete
            </Button></li>
        ))}
    </ul>
    </Container>);
};

export default BannerManagement;
