import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';



const GoogleMapComponent = () => {
    const [shops, setShops] = useState([]);
  
    useEffect(() => {
      fetch('http://localhost:3004/shops')
        .then(response => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then(data => {
          setShops(data);
        })
        .catch(error => console.error('Error fetching data:', error));
    }, []);
  
    // Calculate the average latitude and longitude of all shops
    const averageLat = shops.reduce((sum, shop) => sum + shop.lat, 0) / shops.length;
    const averageLng = shops.reduce((sum, shop) => sum + shop.lng, 0) / shops.length;
  
    // Set the center to the average coordinates
    const center = {
      lat: isNaN(averageLat) ? 0 : averageLat,
      lng: isNaN(averageLng) ? 0 : averageLng,
    };
  
    return (
      <LoadScript googleMapsApiKey="AIzaSyAjKkYqxRTixO9xOe3R6UIULNMQyU7sUGo">
        <GoogleMap mapContainerStyle={mapContainerStyle} center={center} zoom={10}>
          {shops.map(shop => (
            <Marker
              key={shop.id}
              position={{ lat: shop.lat, lng: shop.lng }}
              // Customize marker as needed
              icon={{ url: 'path-to-your-marker-image.png', scaledSize: new window.google.maps.Size(30, 30) }}
            />
          ))}
        </GoogleMap>
      </LoadScript>
    );
  };
  
  export default GoogleMapComponent;
