// GoogleMap.jsx
import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const mapContainerStyle = { width: '100%', height: '400px' };

const center = {
  lat: 0, // Set to your default latitude
  lng: 0, // Set to your default longitude
};

const GoogleMapComponent = () => {
  const [shops, setShops] = useState([]);

  useEffect(() => {
    // Fetch data from your Node.js backend using the fetch function
    fetch('http://localhost:3004/shops')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setShops(data);
        console.log('Data from fetch:', data); // Add this line to log the data
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <LoadScript googleMapsApiKey="AIzaSyAjKkYqxRTixO9xOe3R6UIULNMQyU7sUGo">
      <GoogleMap mapContainerStyle={mapContainerStyle} center={center} zoom={10}>
        {shops.map(shop => (
          <Marker
            key={shop.id}
            position={{ lat: shop.lat, lng: shop.lng }}
            // Customize marker as needed
            icon={{ url: 'path-to-your-marker-image.png', scaledSize: new window.google.maps.Size(30, 30) }}
          />
        ))}
      </GoogleMap>
    </LoadScript>
  );
};

export default GoogleMapComponent;
