import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Container } from 'react-bootstrap';

function LoginAdminPage({ AdminonLogin }) {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();


    const handleAdminLogin = async () => {
        try {
        const response = await axios.post("api/adminlogin", { username, password, KEY });
        if (response.status === 200) {
    
            AdminonLogin();
    
            navigate("/adminpage");
        } else {
            setError("Login failed");
        }
        } catch (error) {
        setError("Login Failed, Please check your username and password");
        }}

return (
      <div style={{ background: '#3cdbde', minHeight: '100vh', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Container style={{ width: '700px',height: '800px' , marginTop: '20px', padding: '20px', border: '10px solid black', borderRadius: '8px' }}>
          <h2 style={{margin: '30px', fontSize : "rem"}}>  ADMIN LOGIN</h2>
          <form onSubmit={handleAdminLogin} style={{ display: 'flex', flexDirection: 'column' }}>
            <label htmlFor="username">Username:</label>
            <input
              onChange={handleChange}
              name="username"
              value={contact.username}
              placeholder="Username"
              style={{ marginBottom: '10px' }}
            />
  
            <label htmlFor="password">Password:</label>
            <input
              onChange={handleChange}
              name="password"
              value={contact.password}
              placeholder="Password"
              type="password"
              style={{ marginBottom: '10px' }}
            />
  
            <label htmlFor="email">KEY:</label>
            <input
              onChange={handleChange}
              name="email"
              value={contact.email}
              placeholder="KEY"
              type="password"
              style={{ marginBottom: '10px' }}
            />
  
            <button onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} 
            type="submit" style={{ marginTop: '30px', padding:'10px' , fontWeight : "bold" ,transition: 'background-color 0.3s', backgroundColor: isMousedOver ? "black" : "whitesmoke" , color : "#40eb42"}}>Log in</button>
          </form>
          <img style={{display: 'block',  border: '2px solid black' , padding : '30px' , margin: 'auto', marginTop : '150px' , backgroundColor : "white"}}
          
          src="https://feyverly.com/wp-content/uploads/2020/04/cropped-Feyverlylogo.jpg" alt="Feyverly Logo" />
        </Container>
        {errorMessage && <p>{errorMessage}</p>}
      </div>
    );
  }
  
  export default LoginAdminPage;
