import React, { useState } from "react";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Container } from 'react-bootstrap';

function Register() {
  const api_url = "http://localhost:3004";
  const [contact, setContact] = useState({
    username : "",
    password: "",
    email: "",
  });
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setContact({
      ...contact,
      [name]: value,
    });
  };

  const handleSubmit = async () => {


    try {

      const response = await axios.post(`${api_url}/register`, contact);

      console.log("Registration successful:", response.data);


      navigate('/userlogin');
    } catch (error) {

      console.error("Registration failed:", error.response.data);
      setErrorMessage('Registration failed. Please try again.');
    }
  };

  return (
    <Container style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop : '100px' ,border: '5px solid red', borderRadius: '8px'}}>
      <h1 >WELCOME TO FEYVERLY SERVICE</h1>
      <div style={{ width: '300px', marginTop: '20px' }}>
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column' }} >
        <label htmlFor="username">Username:</label>
          <input
            onChange={handleChange}
            name="username"
            value={contact.username}
            placeholder="Username"
            style={{ marginBottom: '10px' }}
          />
          <label htmlFor="username">Password:</label>
          <input 
            onChange={handleChange}
            name="password"
            value={contact.password}
            placeholder="Password"
            type="password"
            style={{ marginBottom: '10px' }}
          />
          <label htmlFor="username">Email:</label>
          <input
            onChange={handleChange}
            name="email"
            value={contact.email}
            placeholder="Email"
            type="email"
            style={{ marginBottom: '10px' }}
          />

        </form>
        <button type="submit" style={{ marginBottom: '10px' }}>Submit</button>
      </div>
      {errorMessage && <p>{errorMessage}</p>}
    </Container>
  );
}

export default Register;
