import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminManagement = () => {
  const [selectedTable, setSelectedTable] = useState('');
  const [tables, setTables] = useState([]);
  const [crudAction, setCrudAction] = useState('');
  const [data, setData] = useState([]);
  const [formData, setFormData] = useState({});

  // Fetch available tables when the component mounts
  useEffect(() => {
    const fetchTables = async () => {
      try {
        const response = await axios.get('http://localhost:3004/tables');
        setTables(response.data);
      } catch (error) {
        console.error('Error fetching tables:', error);
      }
    };

    fetchTables();
  }, []);

  // Fetch data for the selected table
  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:3004/${selectedTable}`);
      setData(response.data);
    } catch (error) {
      console.error(`Error fetching data for ${selectedTable}:`, error);
    }
  };

  // Perform CRUD operation
  const handleCrud = async () => {
    try {
      if (crudAction === 'get') {
        await fetchData();
      } else {
        const response = await axios[crudAction](`http://localhost:3004/${selectedTable}`, formData);
        console.log(response.data);
        // Optionally, you can fetch the updated data after a successful CRUD operation
        await fetchData();
      }
    } catch (error) {
      console.error(`Error ${crudAction} data for ${selectedTable}:`, error);
    }
  };

  return (
    <div>
      <h1>Admin Management</h1>
      <label>
        Select Table:
        <select value={selectedTable} onChange={(e) => setSelectedTable(e.target.value)}>
          <option value="">Select a table</option>
          {tables.map((table) => (
            <option key={table} value={table}>
              {table}
            </option>
          ))}
        </select>
      </label>
      <label>
        Select Action:
        <select value={crudAction} onChange={(e) => setCrudAction(e.target.value)}>
          <option value="">Select an action</option>
          <option value="get">Get</option>
          <option value="post">Post</option>
          <option value="patch">Patch</option>
          <option value="delete">Delete</option>
        </select>
      </label>
      <button onClick={handleCrud}>Submit</button>
      {/* Display data if available */}
      {data.length > 0 && (
        <div>
          <h2>{`Data for ${selectedTable}`}</h2>
          <ul>
            {data.map((item) => (
              <li key={item.id}>{JSON.stringify(item)}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default AdminManagement;
