import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const HomePage = ({ onLogin }) => {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    setLoading(true);

    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3004/banners');

        if (response.status === 200) {
          const { token } = response.data;
          localStorage.setItem('jwtToken', token);

          onLogin();
          navigate('/samsaung');
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [onLogin, navigate]);

  const handleImageClick = (product) => {
    console.log('Image clicked:', product);
  };

  return (
    <div>
      <h1>OUR PRODUCTS</h1>
      
      {/* Loading Indicator */}
      {loading && <p>Fetching banners...</p>}

      {/* Images */}
      <div>
        <img
          onClick={() => handleImageClick('SamSungS23')}
          src="https://img.global.news.samsung.com/th/wp-content/uploads/2023/02/Galaxy-S23-Series_KV_Product_2p_LI-1024x724.jpg"
          alt="Slot 1"
        />
        <img
          onClick={() => handleImageClick('Product2')}
          src="https://via.placeholder.com/500"
          alt="Slot 2"
        />
        <img
          onClick={() => handleImageClick('Product3')}
          src="https://via.placeholder.com/500"
          alt="Slot 3"
        />
        <img
          onClick={() => handleImageClick('Product4')}
          src="https://via.placeholder.com/500"
          alt="Slot 4"
        />
      </div>
    </div>
  );
};

export default HomePage;
