import React, { useState } from 'react';
import { Button, Container, Form } from 'react-bootstrap'; 
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function LoginUserPage({onLogin}) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const api_url = "http://localhost:3000";

  
  const LoginAdminpage = () => {
    navigate("/adminlogin");
  }

  
  const handleUserLoginClick = async () => {
    onLogin();
  
    try {
      const response = await axios.post(`${api_url}/login`, { username , password });

    if (response.status === 200) {
      
      const { token } = response.data;

      localStorage.setItem('jwtToken', token);

      if (typeof onLogin === 'function') {
        
      }

      navigate("/homepage");
  } else { setError("Login failed"); }

    } catch (error) { setError("Login Failed, Please check your username and password"); } 
    
  };

  return (
    <Container>
      <h1 className="mt-4">Login Page</h1>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form>
        <Form.Group className="mb-3" controlId="formBasicUsername">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>

        <Button variant="primary" onClick={handleUserLoginClick}>
          Login
        </Button>
        <button style={{marginLeft : "-500px"}} onClick={LoginAdminpage} type="button" className="btn btn-success">
            Admin
            </button>
      </Form>
    </Container>
  );
}

export default LoginUserPage;
