import React, { useState } from 'react';
import { Button, Container, Form } from 'react-bootstrap'; 
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function LoginUserPage({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [errorStyle, setErrorStyle] = useState('');
  const navigate = useNavigate();
  const api_url = "http://localhost:3004";

  

  const handleUserLoginClick = async () => {
    try {
      const response = await axios.post(`${api_url}/login`, { username, password });

      if (response.status === 200) {
        const { token } = response.data;

        localStorage.setItem('jwtToken', token);

        onLogin();

        navigate("/homepage");
      } else {
        setErrorMessage("Incorrected password or username");
        setErrorStyle( {borderStyle : "bold", borderColor : "red" });
        
      }
    } catch (error) {
      setErrorMessage("Incorrected password or username");
    }
  };

  const LoginAdminpage = () => {
    navigate("/adminlogin");
  }
  return (
    <Container>
      <h1 className="mt-4" style={errorStyle}>Login Page {errorMessage && <span style={{ color: 'red' }}>{errorMessage}</span>}</h1>
      <Form>
        <Form.Group className="mb-3" controlId="formBasicUsername">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>

        <Button variant="primary" onClick={handleUserLoginClick}>
          Login
        </Button>
        <button style={{marginLeft: 'auto', display: 'block', marginTop: '-40px'}} onClick={LoginAdminpage} type="button" className="btn btn-success">
            Admin
        </button>
      </Form>
    </Container>
  );
}

export default LoginUserPage;
