// AdminManagement.js
import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
 // Import the BannersEdit component
import axios from 'axios';

const AdminManagement = () => {
  const [selectedTable, setSelectedTable] = useState('');
  const [tables, setTables] = useState([]);
  const [crudAction, setCrudAction] = useState('');
  const [data, setData] = useState([]);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    const fetchTables = async () => {
      try {
        const response = await axios.get('http://localhost:3004/tables');
        setTables(response.data);
      } catch (error) {
        console.error('Error fetching tables:', error);
      }
    };

    fetchTables();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:3004/${selectedTable}`);
      setData(response.data);
    } catch (error) {
      console.error(`Error fetching data for ${selectedTable}:`, error);
    }
  };

  const handleCrud = async () => {
    try {
      if (crudAction === 'get') {
        await fetchData();
      } else {
        // You can implement other CRUD actions here
        // For now, let's assume there's a separate component for BannersEdit
        // and we'll render it when the selected table is 'banners'
        if (selectedTable === 'banners') {
          // Render the BannersEdit component
          // Note: You may need to pass additional props to the BannersEdit component if required
          return <BannersEdit />;
        }
      }
    } catch (error) {
      console.error(`Error ${crudAction} data for ${selectedTable}:`, error);
    }
  };

  return (
    <div style={{ background: '#3cdbde', minHeight: '100vh', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <Container style={{ width: '700px', height: '800px', marginTop: '20px', padding: '20px', border: '10px solid black', borderRadius: '8px', backgroundColor: 'white', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <h1 style={{ margin: '30px', fontSize: '1rem' }}>Admin Management</h1>
        <div style={{ marginBottom: '20px' }}>
          {tables.map((table) => (
            <a
              key={table}
              href="#"
              onClick={() => setSelectedTable(table)}
              style={{
                margin: '0 10px',
                padding: '5px 10px',
                textDecoration: 'none',
                backgroundColor: selectedTable === table ? '#40eb42' : 'whitesmoke',
                color: selectedTable === table ? 'white' : 'black',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
            >
              {table}
            </a>
          ))}
        </div>
        <label>
          Select Action:
          <select value={crudAction} onChange={(e) => setCrudAction(e.target.value)} style={{ marginBottom: '10px' }}>
            <option value="">Select an action</option>
            <option value="get">Get</option>
            <option value="post">Post</option>
            <option value="patch">Patch</option>
            <option value="delete">Delete</option>
          </select>
        </label>
        <button
          onClick={handleCrud}
          style={{
            marginTop: '10px',
            padding: '10px',
            fontWeight: 'bold',
            transition: 'background-color 0.3s',
            backgroundColor: '#40eb42',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
        >
          Submit
        </button>
        {handleCrud()} {/* Render the component based on the selected table and action */}
      </Container>
    </div>
  );
};

export default AdminManagement;
