import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Form, Button, Table } from 'react-bootstrap';

const AdminBanners = () => {
  const [banners, setBanners] = useState([]);
  const [formData, setFormData] = useState({
    img_brand_url: '',
    img_promotion_url: '',
    content: '',
    topic: '',
  });

  useEffect(() => {
    fetchBanners();
  }, []);

  const fetchBanners = async () => {
    try {
      const response = await axios.get('http://localhost:3004/banners');
      setBanners(response.data);
    } catch (error) {
      console.error('Error fetching banners:', error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleCreateBanner = async () => {
    try {
      const response = await axios.post('http://localhost:3004/banners', formData);
      setBanners([...banners, response.data]);
      setFormData({
        img_brand_url: '',
        img_promotion_url: '',
        content: '',
        topic: '',
      });
    } catch (error) {
      console.error('Error creating banner:', error);
    }
  };

  const handleUpdateBanner = async (id) => {
    try {
      await axios.patch(`http://localhost:3004/banners/${id}`, formData);
      fetchBanners();
    } catch (error) {
      console.error('Error updating banner:', error);
    }
  };

  return (
    <Container>
      <h1>Banners Management</h1>

      {/* Form for creating a new banner */}
      <Form>
        <Form.Group controlId="img_brand_url">
          <Form.Label>Image Brand URL</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter image brand URL"
            name="img_brand_url"
            value={formData.img_brand_url}
            onChange={handleInputChange}
          />
        </Form.Group>

        <Form.Group controlId="img_promotion_url">
          <Form.Label>Image Promotion URL</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter image promotion URL"
            name="img_promotion_url"
            value={formData.img_promotion_url}
            onChange={handleInputChange}
          />
        </Form.Group>

        <Form.Group controlId="content">
          <Form.Label>Content</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            placeholder="Enter content"
            name="content"
            value={formData.content}
            onChange={handleInputChange}
          />
        </Form.Group>

        <Form.Group controlId="topic">
          <Form.Label>Topic</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter topic"
            name="topic"
            value={formData.topic}
            onChange={handleInputChange}
          />
        </Form.Group>

        <Button variant="primary" onClick={handleCreateBanner}>
          Create Banner
        </Button>
      </Form>

      {/* Table to display banners */}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Image Brand URL</th>
            <th>Image Promotion URL</th>
            <th>Content</th>
            <th>Topic</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {banners.map((banner) => (
            <tr key={banner.id}>
              <td>{banner.id}</td>
              <td>{banner.img_brand_url}</td>
              <td>{banner.img_promotion_url}</td>
              <td>{banner.content}</td>
              <td>{banner.topic}</td>
              <td>
                <Button
                  variant="info"
                  onClick={() => setFormData(banner)} // Set form data for editing
                >
                  Edit
                </Button>{' '}
                <Button variant="danger" onClick={() => handleUpdateBanner(banner.id)}>
                  Update
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default AdminBanners;
