// BannersEdit.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BannersEdit = () => {
  const [data, setData] = useState([]);
  const [formData, setFormData] = useState({});
  const [crudAction, setCrudAction] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3004/banners');
      setData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleCrud = async () => {
    try {
      switch (crudAction) {
        case 'get':
          await fetchData();
          break;
        // Implement other CRUD actions (post, patch, delete) here

        default:
          break;
      }
    } catch (error) {
      console.error(`Error ${crudAction} data:`, error);
    }
  };

  return (
    <div>
      <h2>Banners Edit</h2>
      <label>
        Select Action:
        <select value={crudAction} onChange={(e) => setCrudAction(e.target.value)} style={{ marginBottom: '10px' }}>
          <option value="">Select an action</option>
          <option value="get">Get</option>
          <option value="post">Post</option>
          <option value="patch">Patch</option>
          <option value="delete">Delete</option>
        </select>
      </label>
      <button
        onClick={handleCrud}
        style={{
          marginTop: '10px',
          padding: '10px',
          fontWeight: 'bold',
          transition: 'background-color 0.3s',
          backgroundColor: '#40eb42',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
        }}
      >
        Submit
      </button>
      {data.length > 0 && (
        <div style={{ marginTop: '20px', textAlign: 'center' }}>
          <h2>Data for Banners</h2>
          <ul>
            {data.map((item) => (
              <li key={item.id}>{JSON.stringify(item)}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default BannersEdit;
